import React from 'react'
import { useState } from 'react'
import '../App.css'


const Header = () => {

    // const api = `https://api.dictionaryapi.dev/api/v2/entries/en/${word}`

    const [word,setWord] = useState("")
    const [display,setDisplay] = useState("HEY ASK ME !")

    const api = `https://api.dictionaryapi.dev/api/v2/entries/en/${word}`

    let changeHandler = (event) =>{
        setWord(event.target.value)
        setDisplay(event.target.value)

    }
    let clickHandler = (event) =>{
        if(event.key === 'Enter'){
            fetch(api)
            .then(response =>response.json())
            // .then(apidata => {console.log(apidata)})
            .then(apidata =>{
                if(apidata.cod == 200){
                    console.log(apidata)
                }
                else{
                    alert(apidata.message)
                }
            })
        }
    }
  return (
    <div className='Header'>
        <div className='title'>
            Word Hunting 
        </div>

        <div className='word'>
            {/* <h1>{word}</h1> */}
            <h1>{display}</h1>
        </div>
        <div className='input'>
        <input className='inp-field' type="text" onChange={changeHandler}value={word} placeholder='Enter Here!' onKeyPress={clickHandler}/>

        </div>
    </div>
  )
}

export default Header
