import React,{useState} from "react"

import "./CustomExpense.css"

function CustomExpense(){

    const[entertitle,setEntertitle]=useState('')
    const[enteramount,setEnteramount]=useState('')
    const[enterdate,setEnterdate]=useState('')

    function changetitlehandler(event){
        setEntertitle(event.target.value)

    }
    function changeamounthandler(event){
        setEnteramount(event.target.value)
    }

    function changedatehandler(event){
        setEnterdate(event.target.value)
    }

    function submithandler(event){
        event.preventDefault();

    }
    let getInputs = {
        title : entertitle,
        amount : enteramount,
        date : enterdate,
    }

    console.log(getInputs)


return(
    <div className="custom-exp">
        <form onSubmit={submithandler}>
            <div className="custom-exp-controls">
                <div className="custom-exp-control">
                    <label>Title: </label>
                    <input type="text" value={entertitle} onChange={changetitlehandler}/>     
                </div>

                <div className="custom-exp-control">
                    <label>Amount: </label>
                    <input type="number" value={enteramount} onChange={changeamounthandler}/>
                </div>


                <div className="custom-exp-control">
                    <label>Date: </label>
                    <input type="date" value={enterdate} onChange={changedatehandler}/>
                </div>


                <div className="action">
                    <button type="submit">Add Expense</button>
                </div>
            </div>
        </form>

    </div>


)
}

export default CustomExpense
