import { useState } from "react"
import React from 'react'
function State(){
    // state for onchange
    const [text,setText] = useState("") 
    // state for show type words
    const [word,setWord] = useState("")
    // state for hide the text
    const [hide,setHide] = useState("Hello, Muneeb HOW ARE YOU ?")
    const [show,setShow] = useState("")
    function changehandle(e){
        setText(e.target.value)
        setWord(e.target.value)
    }   

    function hidehandle(){
        setHide("")
    }
    function showhandle(){
        setShow(hide)
    }
    return(
    <div>
        <div>
            <input type="text"  value = {text}onChange={changehandle}></input>
        </div>
        <div>
            <h2>{word}</h2>
        </div>

        <div>
            <button onClick={hidehandle}>Hide</button>
            <button onClick={showhandle}>Show</button>
            <h2>{hide}</h2>
            <h2>{show}</h2>
        </div>
    </div>
    )
}
export default State
