import { useState } from 'react'
import '../App.css'


const Header = () => {


    const [data,setData] = useState({})
    const [word,setWord] = useState("")
    const [display,setDisplay] = useState("HEY ASK ME !")

    const api = `https://api.dictionaryapi.dev/api/v2/entries/en/${word}`

    let changeHandler = (event) =>{
        setWord(event.target.value)
        setDisplay(event.target.value)

    }
    let clickHandler = (event) =>{
        if(event.key === 'Enter'){

            fetch(api)
            .then(response =>  response.json())
            .then(apidata => {
                console.log(apidata)
                apidata.map((wdata,key)=>{
                    console.log(wdata)
                    setData(wdata)
                    // return <h2>{data.word}</h2>
                })
            })
            // .then(apidata =>{
            //     if(apidata.cod === 200){
            //         console.log(apidata)
            //     }
            //     else{
            //         alert(apidata.message)
            //     }
            // })
            // .then(apidata => setData(apidata))
            // console.log(data)
        }
    }
  return (
    <div className='Header'>
        <div className='title'>
            Word Hunting 
        </div>

        <div className='word'>
            {/* <h1>{word}</h1> */}
            <h1>{display}</h1>
        </div>
        <div className='input'>
        <input className='inp-field' type="text" onChange={changeHandler}value={word} placeholder='Enter Here!' onKeyPress={clickHandler}/>

        <div>
            {/* {data[1].meanings[1] ? <h3>{data[1].meanings[1].meaning}</h3>:null } */}
            {/* <h2>{data[0].word}</h2> */}
            <h2>{data.word}</h2>
            {/* <h1>{data[0].meanings[0].definitions[0].definition}</h1> */}
        </div>

        </div>
    </div>
  )
}

export default Header
